import React, { useState } from "react";
import PropTypes from "prop-types";

async function loginUser(credentials) {
  return fetch("http://localhost:9000/login", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(credentials),
  }).then((data) => data.json());
}

export default function Login({ setToken }) {
  const [username, setUserName] = useState();
  const [password, setPassword] = useState();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = await loginUser({
      username,
      password,
    });
    setToken(token);
  };
  return (
    <div>
      <div className="container">
        <form onSubmit={handleSubmit}>
          <div className="container " style={mystylee}>
            <div className="row justify-content-center">
              <div className="col-11 col-lg-5">
                <div>
                  <h1 className="font-weight-light text-center py-5">
                    <span className="text-info">Teacher </span>Log In
                  </h1>
                </div>
                <div className="form-group">
                  <label htmlFor="email">Email *</label>
                  <input
                    type="email"
                    name="email"
                    className="form-control"
                    onChange={(e) => setEmail(e)}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="password">Password *</label>
                  <input
                    type="password"
                    name="passwd"
                    className="form-control"
                    onChange={(e) => setPasswd(e)}
                  />
                </div>

                <button
                  type="submit"
                  className="btn btn-dark float-right"
                  style={{ backgroundColor: "black" }}
                  onClick={() => onSubmitHandler()}
                >
                  Submit
                </button>
                <br></br>
                <div>
                  Not Sigup?! Sign up now{" "}
                  <Link to="/teacher-signup" className="ml-auto mx-3">
                    <button className="btn btn-outline-info ">Sign Up</button>
                  </Link>
                </div>
              </div>
            </div>
          </div>

          <label>
            <p id="login" style={{ color: "black" }}>
              Username
            </p>
            <input type="text" onChange={(e) => setUserName(e.target.value)} />
          </label>
          <label>
            <p style={{ color: "white" }}>Password</p>
            <input
              type="password"
              onChange={(e) => setPassword(e.target.value)}
            />
          </label>
          <div>
            <br></br>
            <br></br>
            <br></br>
            <button
              type="submit"
              style={{
                background: "green",
                color: "white",
                height: "50px",
                width: "100px",
              }}
            >
              Submit
            </button>
          </div>
        </form>
      </div>

      <div className="footer">
        <div
          style={{
            color: "While",
          }}
        ></div>
      </div>
    </div>
  );
}

Login.propTypes = {
  setToken: PropTypes.func.isRequired,
};
