import React, { useState } from "react";
import axios from "axios";

export default function Login() {
  const [emailf, setEmail] = useState(null);
  const [passwdf, setPasswd] = useState(null);
  function emailinput(event) {
    const input = event.target.input;
    setEmail(input);
  }
  function passwdinput(event) {
    const input = event.target.input;
    setPasswd(input);
  }
  const onSubmitHandler = () => {
    const data = new FormData();
    data.append("email", emailf);
    data.append("passwd", passwdf);
    axios.post("http://localhost:3000/login", data);
  };
  return (
    <div className="container my-5 py-5">
      <div className="row justify-content-center">
        <div className="col-11 col-lg-5">
          <form>
            <div>
              <h1 className="font-weight-light text-center py-5">
                <span className="text-info">Log </span>In
              </h1>
            </div>
            <div className="form-group">
              <label htmlFor="email">Email *</label>
              <input
                type="email"
                name="email"
                className="form-control"
                onChange={(e) => emailinput(e)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="email">Password *</label>
              <input
                type="password"
                name="password"
                className="form-control"
                onChange={(e) => passwdinput(e)}
              />
            </div>

            <button
              type="primary"
              className="btn btn-dark float-right"
              style={{ backgroundColor: "black" }}
              onClick={() => onSubmitHandler()}
            >
              Send campaign
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
