import React, { useState } from "react";
import PropTypes from "prop-types";

async function loginUser(credentials) {
  return fetch("http://localhost:9000/login", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(credentials),
  }).then((data) => data.json());
}

export default function Login({ setToken }) {
  const [username, setUserName] = useState();
  const [password, setPassword] = useState();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = await loginUser({
      username,
      password,
    });
    setToken(token);
  };
  return (
    <div>
      <a href="#login" className="home">
        Home
      </a>
      <a href="#login" className="about">
        About
      </a>
      <a href="#login" className="team">
        Team
      </a>

      <div className="heading">SOMOIST</div>
      <div className="illustration"></div>

      <div className="gb"></div>
      <div className="g"></div>
      <div id="google_login"></div>

      <div className="fb"></div>
      <div className="f"></div>
      <div id="somoist_login"></div>

      <div className="footertext">Soil Moisture Detector</div>
      <div className="footertextsh">Soil Moisture Detector</div>

      <div className="bg2"></div>
      <div className="loginbg"></div>
      <div className="robot"></div>
      <div className="login">Login</div>

      <div className="submit"></div>
      <div className="container">
        <form onSubmit={handleSubmit}>
          <label>
            <p id="login" style={{ color: "white" }}>
              Username
            </p>
            <input type="text" onChange={(e) => setUserName(e.target.value)} />
          </label>
          <label>
            <p style={{ color: "white" }}>Password</p>
            <input
              type="password"
              onChange={(e) => setPassword(e.target.value)}
            />
          </label>
          <div>
            <br></br>
            <br></br>
            <br></br>
            <button
              type="submit"
              style={{
                background: "green",
                color: "white",
                height: "50px",
                width: "100px",
              }}
            >
              Submit
            </button>
          </div>
        </form>
      </div>

      <div className="footer">
        <div
          style={{
            color: "While",
          }}
        ></div>
      </div>
    </div>
  );
}

Login.propTypes = {
  setToken: PropTypes.func.isRequired,
};
